import React from 'react';

// component function
const Card = (props) => {
	return (
	// This is JSX not HTML
       <div className='bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
          <img src='https://robohash.org/pepeh?100x100' alt='robots' />
          <div className='tc'>

             <h2>{props.name}</h2>
             <p>{props.nickname}</p>
             <p>Motto: {props.motto}</p>
          </div>
       </div>
    );
}

export default Card;
